import React, { useState } from 'react';

const App = () => {
    // const state = useState();
    // let count = 1;

    const [count, setCount] = useState(0);

    const Increment = () => {
        setCount(count + 1);
    }
    
    const Reset = () => {
        setCount(count);
        console.log(count);
    }

    const Decrement = () => {
        setCount(count - 1);
    }

    return (
        <>
            <h1> {count} </h1>
            <div className='btns'>
             <button onClick={Increment}> + </button>
             <button onClick={Reset}> Reset </button>
             <button onClick={Decrement}> - </button>
            </div>
        </>
    )
}

export default App;
