import React, { useState } from 'react';

const App = () => {
    const state = useState();
    // let count = 1;

    const [count, setCount] = useState(0);

    const Increment = () => {
        setCount(count + 1);
}
    return (
        <>
            <h1> {count} </h1>
            <div className='btns'>
             <button onClick={Increment}>  </button>
             <button onClick={Reset}> Add +1 </button>
             <button onClick={Decrement}> Add +1 </button>
            </div>
        </>
    )
}

export default App;
